parameters.jsCode:
const BLACKLIST = [' 驻转', '驻'];

  const PRODUCT_CODES = {
    PENSION: [1, 2, 19],
    GEMEL: [3, 5, 6],
    GEMEL_INVEST: [4],
    HISHTALMUT: [7],
    SAVINGS: [41],
    INSURANCE: [13, 14, 15]
  };

  const GEMELNET_RESOURCE = 'a30dcbea-a1d2-482c-ae29-8f781f5025fb';       
  const PENSIANET_RESOURCE = '6d47d6b5-cb08-488b-b333-f1e717b1e1bd';      
  const BITUACHNET_RESOURCE = 'c6c62cc7-fe02-4b18-8f3e-813abfbb4647';     
  const DATA_GOV_BASE = 'https://data.gov.il/api/3/action/datastore_search';

  function getProductTypeName(code) {
    const types = {
      1: '拽专 驻住', 2: '拽专 驻住', 19: '拽专 驻住',
      3: '拽驻转 ', 4: ' 砖拽注', 5: '拽驻转 ', 6: '拽驻转 ',       
      7: '拽专 砖转转',
      13: ' ', 14: ' ', 15: ' ',
      41: '驻住转 住'
    };
    return types[code] || ' 注';
  }

  function getProductCategory(code) {
    if (PRODUCT_CODES.PENSION.includes(code)) return 'pension';
    if (PRODUCT_CODES.GEMEL.includes(code)) return 'gemel';
    if (PRODUCT_CODES.GEMEL_INVEST.includes(code)) return 'gemel_invest'; 
    if (PRODUCT_CODES.HISHTALMUT.includes(code)) return 'hishtalmut';     
    if (PRODUCT_CODES.SAVINGS.includes(code)) return 'savings';
    if (PRODUCT_CODES.INSURANCE.includes(code)) return 'insurance';       
    return 'other';
  }

  function getProductBadgeClass(code) {
    if (PRODUCT_CODES.PENSION.includes(code)) return 'badge-pension';     
    if (PRODUCT_CODES.HISHTALMUT.includes(code)) return 'badge-hishtalmut';
    if (PRODUCT_CODES.GEMEL.includes(code) || PRODUCT_CODES.GEMEL_INVEST.includes(code)) return 'badge-gemel';
    if (PRODUCT_CODES.INSURANCE.includes(code)) return 'badge-insurance'; 
    if (PRODUCT_CODES.SAVINGS.includes(code)) return 'badge-savings';     
    return '';
  }

  function getStatusInfo(product) {
    const status = product.status || product.maamad?.status;
    if (!status) return { code: 0, description: ' 注' };
    return { code: status.code || 0, description: status.description || (status.code === 1 ? '驻注' : ' 驻注') };
  }

  function getEmploymentStatus(code) {
    return code === 1 ? '砖专' : '注爪';
  }

  function needsGemelNet(typeCode) {
    return PRODUCT_CODES.GEMEL.includes(typeCode) || PRODUCT_CODES.GEMEL_INVEST.includes(typeCode) || PRODUCT_CODES.HISHTALMUT.includes(typeCode);  
  }

  function needsPensionNet(typeCode) {
    return PRODUCT_CODES.PENSION.includes(typeCode);
  }

  function needsBituachNet(typeCode) {
    return PRODUCT_CODES.SAVINGS.includes(typeCode);
  }

  // 住 驻住- (拽驻/转)
  function getPensionClassification(productTypeCode) {
    if (productTypeCode === 1) return '拽专转 砖转';
    if (productTypeCode === 19) return '拽专转 转';
    return null;
  }

  // 住 -
  function getFundClassification(productTypeCode) {
    if (productTypeCode === 7) return '拽专转 砖转转';
    if (productTypeCode === 4) return '拽驻转  砖拽注';
    if ([3, 5, 6].includes(productTypeCode)) return '转 砖转 驻爪';
    return null;
  }

  // URL 驻 .驻 专 + 住
  function buildGemelNetUrl(companyIdNumber, productTypeCode) {
    const classification = getFundClassification(productTypeCode);        
    const filters = { MANAGING_CORPORATION_LEGAL_ID: Number(companyIdNumber) };
    if (classification) {
      filters.FUND_CLASSIFICATION = classification;
    }
    return DATA_GOV_BASE + '?resource_id=' + GEMELNET_RESOURCE + '&filters=' + encodeURIComponent(JSON.stringify(filters)) + '&sort=REPORT_PERIOD%20desc&limit=100';
  }

  function buildPensionNetUrl(companyIdNumber, productTypeCode) {
    const classification = getPensionClassification(productTypeCode);
    const filters = { MANAGING_CORPORATION_LEGAL_ID: Number(companyIdNumber) };
    if (classification) {
      filters.FUND_CLASSIFICATION = classification;
    }
    return DATA_GOV_BASE + '?resource_id=' + PENSIANET_RESOURCE + '&filters=' + encodeURIComponent(JSON.stringify(filters)) + '&sort=REPORT_PERIOD%20desc&limit=100';
  }

  //  砖专 驻 FUND_ID
  function buildBituachNetUrl(trackCode) {
    const filters = { FUND_ID: Number(trackCode) };
    return DATA_GOV_BASE + '?resource_id=' + BITUACHNET_RESOURCE + '&filters=' + encodeURIComponent(JSON.stringify(filters)) + '&sort=REPORT_PERIOD%20desc&limit=1';
  }

  function daysDiff(dateStr) {
    if (!dateStr) return 999;
    const date = new Date(dateStr);
    const currentDate = new Date();
    return Math.floor((currentDate - date) / (1000 * 60 * 60 * 24));      
  }

  const inputData = $input.first().json;
  const shuranceData = inputData.body || inputData;
  const products = shuranceData.body?.products || shuranceData.products || [];

  const portfolioItems = [];
  const criticalAlerts = [];
  const productTypesSet = new Set();
  const gemelApiCalls = [];
  const pensionApiCalls = [];
  const bituachApiCalls = [];

  // 拽抓 驻 .驻 + 住 爪专
  const processedGemelKeys = new Set();
  const processedPensionKeys = new Set();
  const processedBituachTracks = new Set();

  // Calculate age from birthDate
  function calculateAge(birthDate) {
    if (!birthDate) return null;
    const birth = new Date(birthDate);
    const today = new Date();
    let age = today.getFullYear() - birth.getFullYear();
    const monthDiff = today.getMonth() - birth.getMonth();
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birth.getDate())) {
      age--;
    }
    return age;
  }

  // Customer info comes directly from the HTTP request (from Get Customer node)
  const birthDate = shuranceData.birthDate || null;
  const calculatedAge = calculateAge(birthDate);

  const customerInfo = {
    customerName: shuranceData.customerName || '拽',
    customerId: shuranceData.idNumber || null,
    age: calculatedAge,
    birthDate: birthDate,
    employmentStatus: null,
    address: shuranceData.address || '',
    phone: shuranceData.phone || '',
    email: shuranceData.email || ''
  };

  for (const product of products) {
    const productTypeCode = product.type?.code;
    const productType = getProductTypeName(productTypeCode);
    const productCategory = getProductCategory(productTypeCode);
    const badgeClass = getProductBadgeClass(productTypeCode);
    const statusInfo = getStatusInfo(product);
    const employmentStatus = getEmploymentStatus(product.maamad?.code);   
    const companyName = product.companyName || '';
    const companyId = product.companyId || 0;
    const companyIdNumber = product.companyIdNumber || 0;
    const fundCode = product.misparMemHei || null;

    if (productTypeCode) productTypesSet.add(productTypeCode);
    if (!customerInfo.employmentStatus) customerInfo.employmentStatus = employmentStatus;

    if (PRODUCT_CODES.PENSION.includes(productTypeCode) && statusInfo.code === 2) {
      criticalAlerts.push({
        severity: 'CRITICAL', type: 'RISK_STATUS',
        productName: product.shemTohnit || productType,
        productNumber: product.accountPolicyNumber || '',
        message: ' 拽专 驻住 "' + (product.shemTohnit || productType) +
 '" 爪 专住拽 !',
        action: '专砖 驻  - 拽  注住拽'
      });
    }

    if (employmentStatus === '砖专' && PRODUCT_CODES.PENSION.includes(productTypeCode)) {
      const lastDeposit = product.lastDeposit?.hafkadot?.[0];
      if (lastDeposit && lastDeposit.taarihEreh) {
        const daysGap = daysDiff(lastDeposit.taarihEreh);
        if (daysGap >= 60) {
          criticalAlerts.push({
            severity: 'HIGH', type: 'MISSING_DEPOSITS',
            productName: product.shemTohnit || productType,
            productNumber: product.accountPolicyNumber || '',
            lastDeposit: lastDeposit.taarihEreh, daysGap: daysGap,        
            message: '锔  转拽 驻拽 -"' + (product.shemTohnit || prooductType) + '"  ' + lastDeposit.taarihEreh + ' (' + daysGap + ' )',
            action: '砖 拽  注住拽'
          });
        }
      }
    }

    if (product.masluleiAshkaa && product.masluleiAshkaa.length > 0) {    
      for (const maslul of product.masluleiAshkaa) {
        const trackCode = maslul.code;
        const trackKey = String(trackCode);

        portfolioItems.push({
          fundCode, trackCode, productTypeCode, productType, productCategory,
          productBadgeClass: badgeClass,
          productName: product.shemTohnit || productType,
          productNumber: product.accountPolicyNumber || '',
          companyId, companyIdNumber, companyName,
          trackName: maslul.name,
          trackAccumulation: maslul.accumulation || 0,
          trackPercent: Math.round((maslul.accumulation / (product.accumulation || 1)) * 100) || 0,
          productAccumulation: product.accumulation || 0,
          accumulationFee: (product.accumulationFee || 0) * 100,
          depositFee: (product.depositFee || 0) * 100,
          statusCode: statusInfo.code,
          statusDescription: statusInfo.description,
          employmentStatus,
          lastDepositDate: product.lastDeposit?.hafkadot?.[0]?.taarihEreh || null,
          lastDepositMonth: product.lastDeposit?.hafkadot?.[0]?.month || null,
          lastDepositAmount: product.lastDeposit?.hafkadot?.[0]?.total || 0,
          salary: product.maamad?.salary || 0,
          employerName: product.lastDeposit?.hafkadot?.[0]?.shemMaasik || '',
          // Pension-specific fields - using collection objects
          insuredSalary: product.insuredSalary || 0,
          kitzbatNehut: product.kitzbatNehut?.amount || product.pensionDisabilityAllowance || 0,
          kitzbaLeAlman: product.kitzbaLeAlman?.amount || product.partnerAllowance || 0,
          kitzbaLeYetom: product.kitzbaLeYetom?.amount || product.orphanAllowance || 0,
          kitzbatZiknaKolelPremiot: product.kitzbatZiknaKolelPremiot || product.swKitzbatZiknaKolelPremiot || 0,
          retirementAge: product.swGilPrishaLeHishuvTahaziot || 67,
          // Insurance coverage percentages (住 )
          disabilityCoverage: product.pensionDisabilityCoverage || 0,
          partnerCoverage: product.partnerCoverage || 0,
          orphanCoverage: product.orphanCoverage || 0
        });

        // 拽抓 驻 .驻 + 住 爪专
        if (needsGemelNet(productTypeCode) && companyIdNumber) {
          const key = companyIdNumber + '_' + productTypeCode;
          if (!processedGemelKeys.has(key)) {
            processedGemelKeys.add(key);
            gemelApiCalls.push({
              companyIdNumber,
              companyName,
              productTypeCode,
              productType,
              customerTrackCodes: [trackKey],
              apiUrl: buildGemelNetUrl(companyIdNumber, productTypeCode)  
            });
          } else {
            const existing = gemelApiCalls.find(c => c.companyIdNumber === companyIdNumber && c.productTypeCode === productTypeCode);
            if (existing && !existing.customerTrackCodes.includes(trackKey)) {
              existing.customerTrackCodes.push(trackKey);
            }
          }
        } else if (needsPensionNet(productTypeCode) && companyIdNumber) { 
          const key = companyIdNumber + '_' + productTypeCode;
          if (!processedPensionKeys.has(key)) {
            processedPensionKeys.add(key);
            pensionApiCalls.push({
              companyIdNumber,
              companyName,
              productTypeCode,
              productType,
              customerTrackCodes: [trackKey],
              apiUrl: buildPensionNetUrl(companyIdNumber, productTypeCode)
            });
          } else {
            const existing = pensionApiCalls.find(c => c.companyIdNumber === companyIdNumber && c.productTypeCode === productTypeCode);
            if (existing && !existing.customerTrackCodes.includes(trackKey)) {
              existing.customerTrackCodes.push(trackKey);
            }
          }
        } else if (needsBituachNet(productTypeCode) && trackCode) {       
          if (!processedBituachTracks.has(trackKey)) {
            processedBituachTracks.add(trackKey);
            bituachApiCalls.push({
              trackCode: trackKey,
              trackName: maslul.name,
              companyName,
              productType,
              productTypeCode,
              customerTrackCodes: [trackKey],
              apiUrl: buildBituachNetUrl(trackCode)
            });
          }
        }
      }
    }
  }

  const productTypes = Array.from(productTypesSet);
  const summaryStats = {
    totalAccumulation: shuranceData.tzvira?.amount || 0,
    projectedPension: shuranceData.kitzbaHazuyaKolelPremiot?.amount || 0, 
    ytdReturn: shuranceData.tsuaMithilatShana?.amount || 0,
    monthlyDeposit: 0
  };

  for (const product of products) {
    if (product.lastDeposit?.hafkadot?.[0]) {
      summaryStats.monthlyDeposit += product.lastDeposit.hafkadot[0].total || 0;
    }
  }

  return {
    json: {
      customerInfo, portfolioItems, summaryStats, criticalAlerts, productTypes,
      needGemelNet: gemelApiCalls.length > 0,
      needPensionNet: pensionApiCalls.length > 0,
      needBituachNet: bituachApiCalls.length > 0,
      gemelApiCalls, pensionApiCalls, bituachApiCalls,
      blacklist: BLACKLIST,
      productCodes: PRODUCT_CODES
    }
  };

// ===== PARSE GEMEL RESULTS - 注 Top 3 转专转 =====
const results = [];
const splitItems = $('Split Gemel Tracks').all();

for (let i = 0; i < $input.all().length; i++) {
  const item = $input.all()[i];
  const splitItem = splitItems[i]?.json || {};
  const records = item.json?.result?.records || [];
  const customerTrackCodes = splitItem.customerTrackCodes || [];

  // 砖 1: 专砖 专  FUND_ID
  const latestByFundId = {};
  for (const rec of records) {
    const fundId = String(rec.FUND_ID);
    if (!latestByFundId[fundId] || rec.REPORT_PERIOD > latestByFundId[fundId].REPORT_PERIOD) {
      latestByFundId[fundId] = rec;
    }
  }

  // 砖 2: 转 专砖转  住 注 砖 砖驻
  const allTracks = Object.values(latestByFundId).map(rec => {
    const assets = parseFloat(rec.TOTAL_ASSETS || 1);
    const exposure = parseFloat(rec.STOCK_MARKET_EXPOSURE || 0);
    return {
      fundId: String(rec.FUND_ID),
      fundName: rec.FUND_NAME || '',
      ytdYield: parseFloat(rec.YEAR_TO_DATE_YIELD || 0),
      yield3Y: parseFloat(rec.AVG_ANNUAL_YIELD_TRAILING_3YRS || 0),
      stockExposure: assets > 0 ? (exposure / assets) * 100 : 0,
      totalAssets: assets,
      managingCorp: rec.MANAGING_CORPORATION || '',
      fundClassification: rec.FUND_CLASSIFICATION || '',
      mgmtFee: parseFloat(rec.AVG_ANNUAL_MANAGEMENT_FEE || 0)
    };
  });

  // 砖 3:  驻 转砖 ( )
  allTracks.sort((a, b) => b.ytdYield - a.ytdYield);

  // 砖 4: Top 3
  const top3 = allTracks.slice(0, 3).map((t, idx) => ({
    ...t,
    rank: idx + 1
  }));

  // 砖 5: 专转 专砖  住 砖 拽
  for (const trackCode of customerTrackCodes) {
    const customerTrack = allTracks.find(t => t.fundId === trackCode);

    // 拽转 专: 砖驻  (卤15%) + 转砖  -7%+
    let hasAlert = false;
    let betterTrack = null;

    if (customerTrack) {
      for (const track of allTracks) {
        if (track.fundId === trackCode) continue;
        const exposureDiff = Math.abs(track.stockExposure - customerTrack.stockExposure);
        const yieldDiff = track.ytdYield - customerTrack.ytdYield;
        if (exposureDiff <= 15 && yieldDiff >= 7) {
          hasAlert = true;
          betterTrack = track;
          break;
        }
      }
    }

    // 砖 专
    const rankIndex = allTracks.findIndex(t => t.fundId === trackCode);
    const ranking = rankIndex >= 0 ? rankIndex + 1 : null;

    results.push({
      json: {
        trackCode,
        trackName: customerTrack?.fundName || splitItem.trackName || '',
        productTypeCode: splitItem.productTypeCode,
        companyId: splitItem.companyId,
        companyName: splitItem.companyName,
        // 转 住 
        stockExposure: customerTrack?.stockExposure || null,
        ytdYield: customerTrack?.ytdYield || null,
        yield3Y: customerTrack?.yield3Y || null,
        totalAssets: customerTrack?.totalAssets || null,
        managingCorp: customerTrack?.managingCorp || '',
        fundClassification: customerTrack?.fundClassification || '',
        mgmtFee: customerTrack?.mgmtFee || null,
        // 爪转
        ranking,
        totalTracks: allTracks.length,
        top3,
        allTracks,
        hasAlert,
        betterTrack,
        // 
        source: 'GEMELNET',
        hasData: customerTrack !== undefined
      }
    });
  }
}

return results;

// ===== PARSE PENSION RESULTS - 注 Top 3 转专转 =====
const results = [];
const splitItems = $('Split Pension Tracks').all();

for (let i = 0; i < $input.all().length; i++) {
