    if (customerTrack) {
      for (const track of allTracks) {
        if (track.fundId === trackCode) continue;
        const exposureDiff = Math.abs(track.stockExposure - customerTrack.stockExposure);
        const yieldDiff = track.ytdYield - customerTrack.ytdYield;
        if (exposureDiff <= 15 && yieldDiff >= 7) {
          hasAlert = true;
          betterTrack = track;
          break;
        }
      }
    }

    // 砖 专
    const rankIndex = allTracks.findIndex(t => t.fundId === trackCode);
    const ranking = rankIndex >= 0 ? rankIndex + 1 : null;

    results.push({
      json: {
        trackCode,
        trackName: customerTrack?.fundName || splitItem.trackName || '',
        productTypeCode: splitItem.productTypeCode,
        companyId: splitItem.companyId,
        companyName: splitItem.companyName,
        // 转 住 
        stockExposure: customerTrack?.stockExposure || null,
        ytdYield: customerTrack?.ytdYield || null,
        yield3Y: customerTrack?.yield3Y || null,
        totalAssets: customerTrack?.totalAssets || null,
        managingCorp: customerTrack?.managingCorp || '',
        fundClassification: customerTrack?.fundClassification || '',
        mgmtFee: customerTrack?.mgmtFee || null,
        // 爪转
        ranking,
        totalTracks: allTracks.length,
        top3,
        allTracks,
        hasAlert,
        betterTrack,
        // 
        source: 'PENSIANET',
        hasData: customerTrack !== undefined
      }
    });
  }
}

return results;

// ===== PARSE BITUACH RESULTS -  砖 转 =====
const results = [];
const splitItems = $('Split Bituach Tracks').all();

for (let i = 0; i < $input.all().length; i++) {
  const item = $input.all()[i];
  const splitItem = splitItems[i]?.json || {};
  const records = item.json?.result?.records || [];

  let stockExposure = null;
  let yield3Y = null;
  let ytdYield = null;
  let fundClassification = null;
  let managingCorp = null;
  let mgmtFee = null;
  let totalAssets = null;
  let fundName = null;
  let hasData = false;

  if (records.length > 0) {
    const latest = records[0];
    const exposure = parseFloat(latest.STOCK_MARKET_EXPOSURE || 0);
    const assets = parseFloat(latest.TOTAL_ASSETS || 1);
    if (assets > 0) stockExposure = (exposure / assets) * 100;
    yield3Y = parseFloat(latest.AVG_ANNUAL_YIELD_TRAILING_3YRS || 0);
    ytdYield = parseFloat(latest.YEAR_TO_DATE_YIELD || 0);
    fundClassification = latest.FUND_CLASSIFICATION || '';
    managingCorp = latest.MANAGING_CORPORATION || '';
    mgmtFee = parseFloat(latest.AVG_ANNUAL_MANAGEMENT_FEE || 0);
    totalAssets = assets;
    fundName = latest.FUND_NAME || '';
    hasData = true;
  }

  results.push({
    json: {
      trackCode: String(splitItem.trackCode),
      trackName: splitItem.trackName,
      productTypeCode: splitItem.productTypeCode,
      stockExposure, ytdYield, yield3Y,
      fundClassification, managingCorp, mgmtFee, totalAssets, fundName,
      // -  top3 爪转
      ranking: null, totalTracks: null, top3: [], hasAlert: false, betterTrack: null,
      source: 'BITUACHNET',
      hasData
    }
  });
}

return results;

const portfolioData = $('Parse Portfolio + Alerts').first().json;       
 const marketDataInputs = $('Merge Market Data').all();

 const PRODUCT_CODES = portfolioData.productCodes || {
   PENSION: [1, 2, 19],
   GEMEL: [3, 5, 6],
   GEMEL_INVEST: [4],
   HISHTALMUT: [7],
   SAVINGS: [41],
   INSURANCE: [13, 14, 15]
 };

 const APPROVED_COMPANIES = [
   '  驻住 注"',
   '住 拽驻转  注"',
   '专  驻住 注"',
   ' 拽驻转 拽专转 驻住 拽驻转  注"',
   '专 驻住  注"',
   '驻拽住 驻住  注"',
   '砖专 砖  驻住 注"'
 ];

 const mergedData = marketDataInputs.map(item => item.json);
 const BLACKLIST = portfolioData.blacklist || [];
 const MIN_YIELD_GAP_FOR_ALERT = 7;

 const analyzed = [];
 const recommendations = [];

 function isApprovedCompany(companyName) {
   if (!companyName) return false;
   return APPROVED_COMPANIES.some(approved =>
     companyName.includes(approved) || approved.includes(companyName)    
   );
 }

 function isInBlacklist(companyName) {
   if (!companyName) return false;
