   const currentRankIndex = top3SameCompany.findIndex(m => String(m.fundId) === String(trackCode));
   const isInTop3 = currentRankIndex >= 0 && currentRankIndex < 3;

   // 转专 专拽 : 驻注专 >= 7%, 住  砖 , 住  -Top 3
   const hasAlert = gap >= MIN_YIELD_GAP_FOR_ALERT && bestInCompany && String(bestInCompany.fundId) !== String(trackCode) && !isInTop3;

   const currentRank = marketTrack.ranking || (currentRankIndex >= 0 ? currentRankIndex + 1 : null);
   const totalInCategory = marketTrack.totalTracks || top3SameCompany.length;

   const categoryAvg = top3SameCompany.length > 0
     ? top3SameCompany.reduce((sum, m) => sum + m.ytdYield, 0) / top3SameCompany.length
     : 0;
   const diffFromAvg = marketYtdYield - categoryAvg;

   let status = 'OK';
