     },
     top3SameCompany: top3SameCompany,
     hasAlert: hasAlert,
     alertGap: gap,
     bestAlternative: hasAlert ? bestInCompany : null,
     status: status,
     statusMessage: statusMessage
   });

   if (hasAlert && bestInCompany) {
     const better = marketTrack.betterTrack || bestInCompany;
