function processAlternatives(track, currentYtd, currentExposure, productTracks) {
    const alternatives = track.top3SameCompany || [];
    if (alternatives.length === 0) {
        return { hasAlert: false, top3Alternatives: [] };
    }

    const currentId = String(track.trackCode || track.trackId || '');
    const customerTrackIds = (productTracks || []).map(t => String(t.trackId));
    const totalProductAccumulation = (productTracks || []).reduce((sum, t) => sum + (t.trackAccumulation || 0), 0);
    const isHighExposure = currentExposure >= HIGH_EXPOSURE_THRESHOLD;

    const filtered = alternatives
        .filter(alt => String(alt.fundId) !== currentId)
        .filter(alt => {
            const altYield = alt.suaMithilatShanaCollection ?? alt.ytdYield ?? 0;
            const altExposure = alt.ahuzHasifaMenayot ?? alt.stockExposure ?? alt.exposure ?? 0;
            const yieldOk = altYield - currentYtd >= YIELD_MARGIN_THRESHOLD;
            let exposureOk;
            if (isHighExposure) {
                exposureOk = altExposure >= HIGH_EXPOSURE_THRESHOLD;
            } else {
                exposureOk = Math.abs(altExposure - currentExposure) <= EXPOSURE_DIFF_THRESHOLD;
            }
            return yieldOk && exposureOk;
        })
        .map(alt => {
            const altYield = alt.suaMithilatShanaCollection ?? alt.ytdYield ?? 0;
            const altExposure = alt.ahuzHasifaMenayot ?? alt.stockExposure ?? alt.exposure ?? 0;
            const efficiency = altExposure > 0 ? (altYield / altExposure) * 10 : 0;
            const altId = String(alt.fundId);
            const isOwned = customerTrackIds.includes(altId);
            let ownedPercent = 0;
            if (isOwned && totalProductAccumulation > 0) {
                const ownedTrack = productTracks.find(t => String(t.trackId) === altId);
                if (ownedTrack) {
                    ownedPercent = ((ownedTrack.trackAccumulation || 0) / totalProductAccumulation * 100).toFixed(1);
                }
            }
            return {
                name: alt.fundName,
                id: alt.fundId,
                yield: altYield,
                exposure: altExposure,
                efficiency: efficiency,
                isOwned: isOwned,
                ownedPercent: ownedPercent,
                isRecommended: true,
                recommendReason: '爪'
            };
        })
        .sort((a, b) => (b.efficiency || 0) - (a.efficiency || 0))
        .slice(0, 3);

    return {
        hasAlert: filtered.length > 0,
        top3Alternatives: filtered
    };
}

