    } else if (diff < -15) {
        return `砖驻 转 砖 (${actualExposure.toFixed(0)}%)  抓 . 转 砖转  转 驻爪 爪.`;
    }
    return `砖驻 转 砖 (${actualExposure.toFixed(0)}%) 转  驻专驻 住 抓.`;
}

const exposureAdvice = getExposureAdvice(avgExpTotal, recommendedExposure, customerAge);

// Calculate yields per category for chart
const categoryYields = {};
for (const [catName, products] of Object.entries(portfolioData)) {
    if (products.length > 0) {
        let totalAcc = 0, weightedYield = 0;
        for (const p of products) {
            for (const t of p.tracks) {
                const acc = t.trackAccumulation || 0;
                const ytd = t.marketData?.ytdYield || 0;
                totalAcc += acc;
                weightedYield += acc * ytd;
            }
        }
        if (totalAcc > 0) {
            categoryYields[catName] = (weightedYield / totalAcc).toFixed(1);
        }
    }
}

