    <script>
        const COMPANY_URLS = ${JSON.stringify(COMPANY_URLS)};
        const portfolioData = ${portfolioJson};
        const categoryOrder = ${JSON.stringify(categoryOrder)};

        const formatCurrency = num => new Intl.NumberFormat('he-IL', { style: 'currency', currency: 'ILS', maximumFractionDigits: 0 }).format(num || 0);
        const formatPercent = num => (num !== null && num !== undefined && !isNaN(num)) ? num.toFixed(1) + '%' : '-';
        const calcEfficiency = (y, exp) => (!exp || exp === 0 || !y) ? 0 : ((y / exp) * 10).toFixed(1);

        function getStarRating(score) {
            const s = parseFloat(score);
            let stars = '';
            for (let i = 1; i <= 5; i++) {
                stars += i <= Math.round(s) ? '<span class="fill"></span>' : '<span></span>';
            }
            return stars;
        }

        function getCategoryIcon(catName) {
            const icons = {
                '驻住': '<svg width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="white" stroke-width="2"><path d="M12 2L2 7l10 5 10-5-10-5z"/><path d="M2 17l10 5 10-5"/><path d="M2 12l10 5 10-5"/></svg>',
                '': '<svg width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="white" stroke-width="2"><path d="M19 5c-1.5 0-2.8 1.4-3 2-3.5-1.5-11-.3-11 5 0 1.8 0 3 2 4.5V20h4v-2h3v2h4v-4c1-.5 1.7-1 2-2h2v-4h-2c0-1-.5-1.5-1-2z"/><path d="M2 9v1c0 1.1.9 2 2 2h1"/></svg>',
                '砖转转': '<svg width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="white" stroke-width="2"><path d="M22 10v6M2 10l10-5 10 5-10 5z"/><path d="M6 12v5c0 2 2 3 6 3s6-1 6-3v-5"/></svg>',
                '砖拽注转 专转': '<svg width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="white" stroke-width="2"><circle cx="12" cy="12" r="8"/><path d="M12 8v8"/><path d="M8 12h8"/></svg>',
                ' ': '<svg width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="white" stroke-width="2"><path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z"/></svg>'
            };
            return icons[catName] || icons['砖拽注转 专转'];
        }

        function getCategoryColor(catName) {
            const colors = {
                '驻住': 'linear-gradient(135deg, #1e3a8a 0%, #3b82f6 100%)',
                '': 'linear-gradient(135deg, #dc2626 0%, #f87171 100%)',
                '砖转转': 'linear-gradient(135deg, #7c3aed 0%, #a78bfa 100%)',
                '砖拽注转 专转': 'linear-gradient(135deg, #059669 0%, #34d399 100%)',
                ' ': 'linear-gradient(135deg, #0891b2 0%, #22d3ee 100%)'
            };
            return colors[catName] || colors['砖拽注转 专转'];
        }

        function getFallbackColor(name) {
            const n = name || '';
            if (n.includes('驻拽住') || n.includes('驻拽住')) return '#f97316';
            if (n.includes('')) return '#4f46e5';
            if (n.includes('专')) return '#2563eb';
            if (n.includes('')) return '#0d9488';
            if (n.includes('')) return '#0891b2';
            if (n.includes('砖专')) return '#16a34a';
            if (n.includes('专')) return '#9333ea';
            if (n.includes('住')) return '#0284c7';
            if (n.includes('专')) return '#e11d48';
            if (n.includes('')) return '#d97706';
            return '#475569';
        }

        function getCompanyLoginUrl(name) {
            for (const [key, url] of Object.entries(COMPANY_URLS)) {
                if (name && name.includes(key)) return url;
            }
            return '#';
        }

        function cleanName(name, companyName) {
            if (!name) return '';
            let cleaned = name;
            if (companyName) {
                cleaned = cleaned.replace(new RegExp(companyName, 'gi'), '');
                const companyFirstWord = companyName.split(' ')[0];
                if (companyFirstWord) cleaned = cleaned.replace(new RegExp(companyFirstWord, 'gi'), '');
            }
            cleaned = cleaned.replace(/注"/g, ' ').replace(/-/g, ' ').replace(/\\s+/g, ' ').trim();
            return cleaned.length > 2 ? cleaned : name;
        }

        function toggle(id) {
            const el = document.getElementById(id);
            const chevron = document.getElementById('chevron-' + id);
            const isOpening = el && !el.classList.contains('open');

            // Determine what level we're toggling
            const isProduct = id.includes('-prod-');
            const isCategory = id.startsWith('cat-') && !isProduct;

            // Close same-level accordions only
            document.querySelectorAll('.accordion-body.open').forEach(function(openEl) {
                const openId = openEl.id;
                const openIsProduct = openId.includes('-prod-') && !openId.includes('-t');
                const openIsCategory = openId.startsWith('cat-') && !openId.includes('-prod-');
                const openIsTrack = openId.includes('-t');

                // If clicking a product, close other products and tracks (but not categories)
                if (isProduct && (openIsProduct || openIsTrack)) {
                    openEl.classList.remove('open');
                    var chev = document.getElementById('chevron-' + openId);
                    if (chev) chev.classList.remove('open');
                }
                // If clicking a category, close other categories (and their children)
                else if (isCategory && openIsCategory) {
                    openEl.classList.remove('open');
                    var chev = document.getElementById('chevron-' + openId);
                    if (chev) chev.classList.remove('open');
                }
            });

            // Now open the clicked one if it was closed
            if (isOpening && el) {
                el.classList.add('open');
                if (chevron) chevron.classList.add('open');
            }
        }

        function toggleTrack(id) {
            const el = document.getElementById(id);
            const isOpening = el && !el.classList.contains('open');

            // Close only other tracks (not products or categories)
            document.querySelectorAll('.accordion-body.open').forEach(function(openEl) {
                if (openEl.id.includes('-t')) {
                    openEl.classList.remove('open');
                    var chevron = document.getElementById('chevron-' + openEl.id);
                    if (chevron) chevron.classList.remove('open');
                }
            });

            // Now open the clicked one if it was closed
            if (isOpening && el) {
                el.classList.add('open');
                var chevron = document.getElementById('chevron-' + id);
                if (chevron) chevron.classList.add('open');
            }
        }

