// ===== INSURANCE REPORT - Policy Accordion with Covers =====
const inputData = $input.first().json;
const shuranceData = inputData.body || inputData;
const allProducts = shuranceData.body?.products || shuranceData.products || [];

const customerName = inputData.customerName || shuranceData.customerName || '拽';
const currentDate = new Date().toLocaleDateString('he-IL');

const insuranceProducts = allProducts.filter(p => {
    const group = p.type?.group;
    const hasCovers = p.covers && p.covers.length > 0;
    const statusDesc = p.status?.description || '';
    const isCancelled = statusDesc === '' || statusDesc.includes('');
    return hasCovers && !isCancelled && (group === '专转' || group === '');
});

const COMPANY_NAMES = {
    1: '专', 12: '驻拽住', 15: '', 16: '砖专', 17: '专',
    25: '专', 28: '', 48: '', 50: '', 51: '',
    61: ' 砖专', 87: '转'
};

const COVERAGE_CATEGORIES = [
    { id: 'life', name: ' ', keywords: ['', '专住拽', '转'] },
    { id: 'health', name: ' 专转', keywords: ['专转', '专驻', '砖转转', '转', '转专驻转', '砖驻'] },
    { id: 'critical', name: ' 拽砖', keywords: [' 拽砖', '转 拽砖转', ' '] },
    { id: 'nursing', name: '住注', keywords: ['住注', '住注'] },
    { id: 'disability', name: ' 砖专 注', keywords: [' 砖专', '"注', '转', ' 砖专'] }
];

function detectCoverage(text) {
    if (!text) return [];
    const found = [];
    COVERAGE_CATEGORIES.forEach(cat => {
        if (cat.keywords.some(kw => text.includes(kw))) found.push(cat.id);
    });
    return found;
}

function formatCurrency(num) {
    return new Intl.NumberFormat('he-IL', { style: 'currency', currency: 'ILS', maximumFractionDigits: 0 }).format(num || 0);
}

function formatDate(dateStr) {
    if (!dateStr) return '-';
    try { return new Date(dateStr).toLocaleDateString('he-IL'); } catch { return '-'; }
}


function renderInsMutavim(mutavim) {
    if (!mutavim || mutavim.length === 0) return '';
    const rows = mutavim.map(m => {
        const name = (m.shemPrati || '') + ' ' + (m.shemMishpaha || '');
        const relation = m.sugZika?.description || '-';
        const purpose = m.mahutMutav?.description || '-';
        const percent = m.ahuzMutav ? (m.ahuzMutav * 100).toFixed(0) + '%' : '-';
        return '<div class="ins-mutav-row"><span class="ins-mutav-name">' + name.trim() + '</span><span class="ins-mutav-relation">' + relation + '</span><span class="ins-mutav-purpose">' + purpose + '</span><span class="ins-mutav-percent">' + percent + '</span></div>';
    }).join('');
    return '<div class="ins-mutavim-section"><div class="ins-mutavim-header"><span class="ins-mutavim-icon"></span><span class="ins-mutavim-title"></span></div><div class="ins-mutavim-list">' + rows + '</div></div>';
}

let insuranceHtml = '';

if (insuranceProducts.length > 0) {
    const insuredMap = new Map();
    let totalPremium = 0;

    insuranceProducts.forEach(product => {
        const companyName = COMPANY_NAMES[product.companyId] || product.companyName || '专';
        const policyNumber = product.accountPolicyNumber || '-';
        const productName = product.shemTohnit || '驻住';
        const productGroup = product.type?.group || '';
        const hasPledge = product.shiabud === true;
        const joinDate = product.policyStartDate || product.startDate || '';
        const status = product.status?.description || '驻注';

        const coversByInsured = {};
        product.covers.forEach(cover => {
            const insuredId = cover.insuredIdNumber || 'unknown';
            if (!coversByInsured[insuredId]) {
                coversByInsured[insuredId] = {
                    insuredId,
                    insuredName: cover.insuredFullName,
                    insuredTypeCode: cover.insuredType?.code || 99,
                    insuredTypeDesc: cover.insuredType?.description || '',
                    covers: []
                };
            }
            coversByInsured[insuredId].covers.push(cover);
        });

        Object.values(coversByInsured).forEach(insuredData => {
            const { insuredId, insuredName, insuredTypeCode, insuredTypeDesc, covers } = insuredData;

            let name = insuredName;
            if (!name) {
                if (insuredTypeCode === 1) name = customerName;
                else if (insuredTypeCode === 2) name = '/转 ';
                else name = '/';
            }

            if (!insuredMap.has(insuredId)) {
                insuredMap.set(insuredId, {
                    id: insuredId, name, typeCode: insuredTypeCode,
                    typeDesc: insuredTypeDesc, policies: [], totalPremium: 0,
                    coverageSet: new Set()
                });
            }

            const insured = insuredMap.get(insuredId);
            if (insuredName) insured.name = insuredName;

            let policyPremium = 0;
            const coverDetails = covers.map(cover => {
                const premium = cover.premia || 0;
                policyPremium += premium;
                totalPremium += premium;

                const allText = (cover.coverDescription || '') + ' ' + (cover.coverTypeDescription || '') + ' ' + productGroup;
                detectCoverage(allText).forEach(cid => insured.coverageSet.add(cid));
                if (productGroup === '') insured.coverageSet.add('life');
                if (productGroup === '专转') insured.coverageSet.add('health');

                return {
                    coverTypeDescription: cover.coverTypeDescription || '',
                    coverDescription: cover.coverDescription || '',
                    coverAmount: cover.insuranceAmount || 0,
                    premium
                };
            });

            insured.totalPremium += policyPremium;

            // Get primary cover type for policy header
            const primaryCoverType = covers[0]?.coverTypeDescription || productGroup;
            const primaryCoverAmount = covers.reduce((sum, c) => sum + (c.coverAmount || 0), 0);

            // Get mutavim (beneficiaries) from product
            const mutavim = product.mutavim || [];

            insured.policies.push({
                policyNumber, companyName, productName, productGroup,
                primaryCoverType,
