<script>
function switchInsTab(t){
    document.querySelectorAll('.ins-report .ins-tab').forEach(x=>x.classList.remove('active'));
    document.querySelectorAll('.ins-report .ins-content').forEach(x=>x.classList.remove('active'));
    document.querySelector('.ins-report [data-tab="'+t+'"]').classList.add('active');
    document.getElementById('tab-'+t).classList.add('active');
}
document.querySelectorAll('.ins-report .ins-tab').forEach(t=>t.addEventListener('click',()=>switchInsTab(t.dataset.tab)));

