// Combine Pension HTML and Insurance HTML
console.log('=== Combine Reports START ===');
const inputs = $input.all();
console.log('Number of inputs:', inputs.length);

let pensionHtml = '';
let insuranceHtml = '';

inputs.forEach((item, idx) => {
    console.log('Input', idx, 'keys:', Object.keys(item.json));
    if (item.json.htmlReport) {
        pensionHtml = item.json.htmlReport;
        console.log('Found pensionHtml, length:', pensionHtml.length);
    }
    if (item.json.insuranceHtml) {
        insuranceHtml = item.json.insuranceHtml;
        console.log('Found insuranceHtml, length:', insuranceHtml.length);
    }
});

// Insert insurance HTML wrapped in a hideable container
let finalHtml = pensionHtml;
if (insuranceHtml && pensionHtml) {
    console.log('Merging pension and insurance HTML');

    // Wrap insurance HTML with a container that's hidden by default
    const wrappedInsuranceHtml = '<div id="insurance-report-content" style="display: none;">' + insuranceHtml + '</div>';

    // Find the floating-footer and insert insurance before it
    const footerIdx = pensionHtml.indexOf('<div class="floating-footer');
    if (footerIdx > -1) {
        finalHtml = pensionHtml.slice(0, footerIdx) + wrappedInsuranceHtml + pensionHtml.slice(footerIdx);
        console.log('Inserted before floating-footer');
    } else {
        // Fallback: insert before </body>
        finalHtml = pensionHtml.replace('</body>', wrappedInsuranceHtml + '</body>');
        console.log('Inserted before </body>');
    }
} else {
    console.log('Missing HTML - pension:', !!pensionHtml, 'insurance:', !!insuranceHtml);
}

console.log('Final HTML length:', finalHtml.length);
console.log('=== Combine Reports END ===');
return { json: { htmlReport: finalHtml } };
